var AstContract = Class.create();
AstContract.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	type: 'AstContract',
	getAstContract:function(){
		var contractno=this.getParameter("sysparm_contractno").trim();
		var glideRecord=new GlideRecord("ast_contract");
		glideRecord.addQuery("short_description","CONTAINS",contractno);
		// 		glideRecord.addActiveQuery();
		glideRecord.query();
		// 		gs.info(">>>>>>JK:"+contractno);
		while(glideRecord.next()){
// 			var remark=glideRecord.description;
// 			remark=remark.replace(/:/g,"@").replace(/ï¼š/g,"@");
// 			var data='{"contract_model":"'+glideRecord. contract_model.display_name+'","starts":"'+glideRecord.starts+'","ends":"'+glideRecord.ends+
// 				'","description":"'+remark+'"}';
				// 			gs.info(">>>>>>JK:data="+data);
			var data=glideRecord. contract_model.display_name+'@'+glideRecord.starts+'@'+glideRecord.ends+'@'+glideRecord.description;
				return data;
			}
			return "";
		}
	});
