var emails='';
function showControlEmailTemplate(){
	var assignTo=g_form.getValue("u_assignto").replace(/\s/g,"").toLowerCase();
	if(assignTo.indexOf("Control".replace(/\s/g,"").toLowerCase())>=0 ){
		sendControl();
	}
}

